// Package ufmt provides utility functions for formatting strings, similarly
// to the Go package "fmt", of which only a subset is currently supported
// (hence the name Âµfmt - micro fmt).
package ufmt

import (
	"errors"
	"strconv"
	"strings"
)

// Println formats using the default formats for its operands and writes to standard output.
// Println writes the given arguments to standard output with spaces between arguments
// and a newline at the end.
func Println(args ...interface{}) {
	var strs []string
	for _, arg := range args {
		switch v := arg.(type) {
		case string:
			strs = append(strs, v)
		case (interface{ String() string }):
			strs = append(strs, v.String())
		case error:
			strs = append(strs, v.Error())
		case int, int8, int16, int32, int64, uint, uint8, uint16, uint32, uint64:
			strs = append(strs, Sprintf("%d", v))
		case bool:
			if v {
				strs = append(strs, "true")
			} else {
				strs = append(strs, "false")
			}
		case nil:
			strs = append(strs, "<nil>")
		default:
			strs = append(strs, "(unhandled)")
		}
	}

	// TODO: remove println after gno supports os.Stdout
	println(strings.Join(strs, " "))
}

// Sprintf offers similar functionality to Go's fmt.Sprintf, or the sprintf
// equivalent available in many languages, including C/C++.
// The number of args passed must exactly match the arguments consumed by the format.
// A limited number of formatting verbs and features are currently supported,
// hence the name ufmt (Âµfmt, micro-fmt).
//
// The currently formatted verbs are the following:
//
//	%s: places a string value directly.
//	    If the value implements the interface interface{ String() string },
//	    the String() method is called to retrieve the value. Same about Error()
//	    string.
//	%c: formats the character represented by Unicode code point
//	%d: formats an integer value using package "strconv".
//	    Currently supports only uint, uint64, int, int64.
//	%t: formats a boolean value to "true" or "false".
//	%%: outputs a literal %. Does not consume an argument.
func Sprintf(format string, args ...interface{}) string {
	// we use runes to handle multi-byte characters
	sTor := []rune(format)
	end := len(sTor)
	argNum := 0
	argLen := len(args)
	buf := ""

	for i := 0; i < end; {
		isLast := i == end-1
		c := string(sTor[i])

		if isLast || c != "%" {
			// we don't check for invalid format like a one ending with "%"
			buf += string(c)
			i++
			continue
		}

		verb := string(sTor[i+1])
		if verb == "%" {
			buf += "%"
			i += 2
			continue
		}

		if argNum > argLen {
			panic("invalid number of arguments to ufmt.Sprintf")
		}
		arg := args[argNum]
		argNum++

		switch verb {
		case "s":
			switch v := arg.(type) {
			case interface{ String() string }:
				buf += v.String()
			case error:
				buf += v.Error()
			case string:
				buf += v
			default:
				buf += fallback(verb, v)
			}
		case "c":
			switch v := arg.(type) {
			// rune is int32. Exclude overflowing numeric types and dups (byte, int32):
			case rune:
				buf += string(v)
			case int:
				buf += string(v)
			case int8:
				buf += string(v)
			case int16:
				buf += string(v)
			case uint:
				buf += string(v)
			case uint8:
				buf += string(v)
			case uint16:
				buf += string(v)
			default:
				buf += fallback(verb, v)
			}
		case "d":
			switch v := arg.(type) {
			case int:
				buf += strconv.Itoa(v)
			case int8:
				buf += strconv.Itoa(int(v))
			case int16:
				buf += strconv.Itoa(int(v))
			case int32:
				buf += strconv.Itoa(int(v))
			case int64:
				buf += strconv.Itoa(int(v))
			case uint:
				buf += strconv.FormatUint(uint64(v), 10)
			case uint8:
				buf += strconv.FormatUint(uint64(v), 10)
			case uint16:
				buf += strconv.FormatUint(uint64(v), 10)
			case uint32:
				buf += strconv.FormatUint(uint64(v), 10)
			case uint64:
				buf += strconv.FormatUint(v, 10)
			default:
				buf += fallback(verb, v)
			}
		case "t":
			switch v := arg.(type) {
			case bool:
				if v {
					buf += "true"
				} else {
					buf += "false"
				}
			default:
				buf += fallback(verb, v)
			}
		// % handled before, as it does not consume an argument
		default:
			buf += "(unhandled verb: %" + verb + ")"
		}

		i += 2
	}
	if argNum < argLen {
		panic("too many arguments to ufmt.Sprintf")
	}
	return buf
}

// This function is used to mimic Go's fmt.Sprintf
// specific behaviour of showing verb/type mismatches,
// where for example:
//
//	fmt.Sprintf("%d", "foo") gives "%!d(string=foo)"
//
// Here:
//
//	fallback("s", 8) -> "%!s(int=8)"
//	fallback("d", nil) -> "%!d(<nil>)", and so on.
func fallback(verb string, arg interface{}) string {
	var s string
	switch v := arg.(type) {
	case string:
		s = "string=" + v
	case (interface{ String() string }):
		s = "string=" + v.String()
	case error:
		// note: also "string=" in Go fmt
		s = "string=" + v.Error()
	case int, int8, int16, int32, int64, uint, uint8, uint16, uint32, uint64:
		// note: rune, byte would be dups, being aliases
		if typename, e := typeToString(v); e != nil {
			panic("should not happen")
		} else {
			s = typename + "=" + Sprintf("%d", v)
		}
	case bool:
		if v {
			s = "bool=true"
		} else {
			s = "bool=false"
		}
	case nil:
		s = "<nil>"
	default:
		s = "(unhandled)"
	}
	return "%!" + verb + "(" + s + ")"
}

// Get the name of the type of `v` as a string.
// The recognized type of v is currently limited to native non-composite types.
// An error is returned otherwise.
func typeToString(v interface{}) (string, error) {
	switch v.(type) {
	case string:
		return "string", nil
	case int:
		return "int", nil
	case int8:
		return "int8", nil
	case int16:
		return "int16", nil
	case int32:
		return "int32", nil
	case int64:
		return "int64", nil
	case uint:
		return "uint", nil
	case uint8:
		return "uint8", nil
	case uint16:
		return "uint16", nil
	case uint32:
		return "uint32", nil
	case uint64:
		return "uint64", nil
	case float32:
		return "float32", nil
	case float64:
		return "float64", nil
	case bool:
		return "bool", nil
	default:
		return "", errors.New("(unsupported type)")
	}
}

// errMsg implements the error interface.
type errMsg struct {
	msg string
}

// Error defines the requirements of the error interface.
// It functions similarly to Go's errors.New()
func (e *errMsg) Error() string {
	return e.msg
}

// Errorf is a function that mirrors the functionality of fmt.Errorf.
//
// It takes a format string and arguments to create a formatted string,
// then sets this string as the 'msg' field of an errMsg struct and returns a pointer to this struct.
//
// This function operates in a similar manner to Go's fmt.Errorf,
// providing a way to create formatted error messages.
//
// The currently formatted verbs are the following:
//
//	%s: places a string value directly.
//	    If the value implements the interface interface{ String() string },
//	    the String() method is called to retrieve the value. Same for error.
//	%c: formats the character represented by Unicode code point
//	%d: formats an integer value using package "strconv".
//	    Currently supports only uint, uint64, int, int64.
//	%t: formats a boolean value to "true" or "false".
//	%%: outputs a literal %. Does not consume an argument.
func Errorf(format string, args ...interface{}) error {
	return &errMsg{Sprintf(format, args...)}
}

package ufmt

import (
	"errors"
	"fmt"
	"testing"
)

type stringer struct{}

func (stringer) String() string {
	return "I'm a stringer"
}

func TestSprintf(t *testing.T) {
	tru := true
	cases := []struct {
		format         string
		values         []interface{}
		expectedOutput string
	}{
		{"hello %s!", []interface{}{"planet"}, "hello planet!"},
		{"hi %%%s!", []interface{}{"worl%d"}, "hi %worl%d!"},
		{"%s %c %d %t", []interface{}{"foo", 'Î±', 421, true}, "foo Î± 421 true"},
		{"string [%s]", []interface{}{"foo"}, "string [foo]"},
		{"int [%d]", []interface{}{int(42)}, "int [42]"},
		{"int8 [%d]", []interface{}{int8(8)}, "int8 [8]"},
		{"int16 [%d]", []interface{}{int16(16)}, "int16 [16]"},
		{"int32 [%d]", []interface{}{int32(32)}, "int32 [32]"},
		{"int64 [%d]", []interface{}{int64(64)}, "int64 [64]"},
		{"uint [%d]", []interface{}{uint(42)}, "uint [42]"},
		{"uint8 [%d]", []interface{}{uint8(8)}, "uint8 [8]"},
		{"uint16 [%d]", []interface{}{uint16(16)}, "uint16 [16]"},
		{"uint32 [%d]", []interface{}{uint32(32)}, "uint32 [32]"},
		{"uint64 [%d]", []interface{}{uint64(64)}, "uint64 [64]"},
		{"bool [%t]", []interface{}{true}, "bool [true]"},
		{"bool [%t]", []interface{}{false}, "bool [false]"},
		{"no args", nil, "no args"},
		{"finish with %", nil, "finish with %"},
		{"stringer [%s]", []interface{}{stringer{}}, "stringer [I'm a stringer]"},
		{"Ã¢", nil, "Ã¢"},
		{"Hello, World! ðŸ˜Š", nil, "Hello, World! ðŸ˜Š"},
		{"unicode formatting: %s", []interface{}{"ðŸ˜Š"}, "unicode formatting: ðŸ˜Š"},
		// mismatch printing
		{"%s", []interface{}{nil}, "%!s(<nil>)"},
		{"%s", []interface{}{421}, "%!s(int=421)"},
		{"%s", []interface{}{"z"}, "z"},
		{"%s", []interface{}{tru}, "%!s(bool=true)"},
		{"%s", []interface{}{'z'}, "%!s(int32=122)"},

		{"%c", []interface{}{nil}, "%!c(<nil>)"},
		{"%c", []interface{}{421}, "Æ¥"},
		{"%c", []interface{}{"z"}, "%!c(string=z)"},
		{"%c", []interface{}{tru}, "%!c(bool=true)"},
		{"%c", []interface{}{'z'}, "z"},

		{"%d", []interface{}{nil}, "%!d(<nil>)"},
		{"%d", []interface{}{421}, "421"},
		{"%d", []interface{}{"z"}, "%!d(string=z)"},
		{"%d", []interface{}{tru}, "%!d(bool=true)"},
		{"%d", []interface{}{'z'}, "122"},

		{"%t", []interface{}{nil}, "%!t(<nil>)"},
		{"%t", []interface{}{421}, "%!t(int=421)"},
		{"%t", []interface{}{"z"}, "%!t(string=z)"},
		{"%t", []interface{}{tru}, "true"},
		{"%t", []interface{}{'z'}, "%!t(int32=122)"},
	}

	for _, tc := range cases {
		name := fmt.Sprintf(tc.format, tc.values...)
		t.Run(name, func(t *testing.T) {
			got := Sprintf(tc.format, tc.values...)
			if got != tc.expectedOutput {
				t.Errorf("got %q, want %q.", got, tc.expectedOutput)
			}
		})
	}
}

func TestErrorf(t *testing.T) {
	tests := []struct {
		name     string
		format   string
		args     []interface{}
		expected string
	}{
		{
			name:     "simple string",
			format:   "error: %s",
			args:     []interface{}{"something went wrong"},
			expected: "error: something went wrong",
		},
		{
			name:     "integer value",
			format:   "value: %d",
			args:     []interface{}{42},
			expected: "value: 42",
		},
		{
			name:     "boolean value",
			format:   "success: %t",
			args:     []interface{}{true},
			expected: "success: true",
		},
		{
			name:     "multiple values",
			format:   "error %d: %s (success=%t)",
			args:     []interface{}{123, "failure occurred", false},
			expected: "error 123: failure occurred (success=false)",
		},
		{
			name:     "literal percent",
			format:   "literal %%",
			args:     []interface{}{},
			expected: "literal %",
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			err := Errorf(tt.format, tt.args...)
			if err.Error() != tt.expected {
				t.Errorf("Errorf(%q, %v) = %q, expected %q", tt.format, tt.args, err.Error(), tt.expected)
			}
		})
	}
}

func TestPrintErrors(t *testing.T) {
	got := Sprintf("error: %s", errors.New("can I be printed?"))
	expectedOutput := "error: can I be printed?"
	if got != expectedOutput {
		t.Errorf("got %q, want %q.", got, expectedOutput)
	}
}

// NOTE: Currently, there is no way to get the output of Println without using os.Stdout,
// so we can only test that it doesn't panic and print arguments well.
func TestPrintln(t *testing.T) {
	tests := []struct {
		name     string
		args     []interface{}
		expected string
	}{
		{
			name:     "Empty args",
			args:     []interface{}{},
			expected: "",
		},
		{
			name:     "String args",
			args:     []interface{}{"Hello", "World"},
			expected: "Hello World",
		},
		{
			name:     "Integer args",
			args:     []interface{}{1, 2, 3},
			expected: "1 2 3",
		},
		{
			name:     "Mixed args",
			args:     []interface{}{"Hello", 42, true, false, "World"},
			expected: "Hello 42 true false World",
		},
		{
			name:     "Unhandled type",
			args:     []interface{}{"Hello", 3.14, []int{1, 2, 3}},
			expected: "Hello (unhandled) (unhandled)",
		},
	}

	// TODO: replace os.Stdout with a buffer to capture the output and test it.
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			Println(tt.args...)
		})
	}
}

// Package ufmt provides utility functions for formatting strings, similarly
// to the Go package "fmt", of which only a subset is currently supported
// (hence the name Âµfmt - micro fmt).
package ufmt

import (
	"errors"
	"strconv"
	"strings"
)

// Println formats using the default formats for its operands and writes to standard output.
// Println writes the given arguments to standard output with spaces between arguments
// and a newline at the end.
func Println(args ...interface{}) {
	var strs []string
	for _, arg := range args {
		switch v := arg.(type) {
		case string:
			strs = append(strs, v)
		case (interface{ String() string }):
			strs = append(strs, v.String())
		case error:
			strs = append(strs, v.Error())
		case int, int8, int16, int32, int64, uint, uint8, uint16, uint32, uint64:
			strs = append(strs, Sprintf("%d", v))
		case bool:
			if v {
				strs = append(strs, "true")
			} else {
				strs = append(strs, "false")
			}
		case nil:
			strs = append(strs, "<nil>")
		default:
			strs = append(strs, "(unhandled)")
		}
	}

	// TODO: remove println after gno supports os.Stdout
	println(strings.Join(strs, " "))
