	for _, tc := range cases {
		name := fmt.Sprintf(tc.format, tc.values...)
		t.Run(name, func(t *testing.T) {
			got := Sprintf(tc.format, tc.values...)
			if got != tc.expectedOutput {
				t.Errorf("got %q, want %q.", got, tc.expectedOutput)
			}
		})
	}
}

func TestErrorf(t *testing.T) {
	tests := []struct {
		name     string
		format   string
		args     []interface{}
		expected string
	}{
		{
			name:     "simple string",
			format:   "error: %s",
			args:     []interface{}{"something went wrong"},
			expected: "error: something went wrong",
		},
		{
			name:     "integer value",
			format:   "value: %d",
			args:     []interface{}{42},
			expected: "value: 42",
		},
		{
			name:     "boolean value",
			format:   "success: %t",
			args:     []interface{}{true},
			expected: "success: true",
		},
		{
			name:     "multiple values",
			format:   "error %d: %s (success=%t)",
			args:     []interface{}{123, "failure occurred", false},
			expected: "error 123: failure occurred (success=false)",
		},
		{
			name:     "literal percent",
			format:   "literal %%",
			args:     []interface{}{},
			expected: "literal %",
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			err := Errorf(tt.format, tt.args...)
			if err.Error() != tt.expected {
				t.Errorf("Errorf(%q, %v) = %q, expected %q", tt.format, tt.args, err.Error(), tt.expected)
			}
		})
	}
}

func TestPrintErrors(t *testing.T) {
	got := Sprintf("error: %s", errors.New("can I be printed?"))
	expectedOutput := "error: can I be printed?"
	if got != expectedOutput {
		t.Errorf("got %q, want %q.", got, expectedOutput)
	}
}

// NOTE: Currently, there is no way to get the output of Println without using os.Stdout,
// so we can only test that it doesn't panic and print arguments well.
func TestPrintln(t *testing.T) {
	tests := []struct {
		name     string
		args     []interface{}
		expected string
	}{
		{
			name:     "Empty args",
			args:     []interface{}{},
			expected: "",
		},
		{
			name:     "String args",
			args:     []interface{}{"Hello", "World"},
			expected: "Hello World",
		},
		{
			name:     "Integer args",
			args:     []interface{}{1, 2, 3},
			expected: "1 2 3",
		},
		{
			name:     "Mixed args",
			args:     []interface{}{"Hello", 42, true, false, "World"},
			expected: "Hello 42 true false World",
		},
		{
			name:     "Unhandled type",
			args:     []interface{}{"Hello", 3.14, []int{1, 2, 3}},
			expected: "Hello (unhandled) (unhandled)",
		},
	}

	// TODO: replace os.Stdout with a buffer to capture the output and test it.
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			Println(tt.args...)
		})
	}
}

package tictactoe

import (
	"errors"
	"std"

	"gno.land/p/grepsuzette/pr2554/v1/ufmt"
)

// this file is @moul's work in #613
// a few changes and bugfixes have been made

type Game struct {
	player1, player2 std.Address
	board            [9]rune // 0=empty, 1=player1, 2=player2
	turnCtr          int
	winnerIdx        int
}

func NewGame(player1, player2 std.Address) (*Game, error) {
	if player1 == player2 {
		return nil, errors.New("cannot fight against self")
	}

	g := Game{
		player1:   player1,
		player2:   player2,
		winnerIdx: -1,
		turnCtr:   -1,
	}
	return &g, nil
}

// Partially recover a game
// The game is guaranteed to be legit in terms of number of tiles 1 and 2
// No winning detection is implemented here however
func RecoverGame(player1, player2 std.Address, board string) (*Game, error) {
	g, e := NewGame(player1, player2)
	if e != nil {
		return nil, e
	}
	if len(board) != 9 {
		return nil, ufmt.Errorf("invalid board length: %d", len(board))
	}
	num1, num2 := 0, 0
	runes := [9]rune{}
	for i, c := range board {
		switch c {
		case rune(0), '_', '-':
			runes[i] = rune(0)
		case rune(1), 'O', 'o':
			num1 += 1
			runes[i] = rune(1)
		case rune(2), 'X', 'x':
			num2 += 1
			runes[i] = rune(2)
		default:
			return nil, errors.New("invalid rune")
		}
	}
	if num1 != num2 && num1 != num2+1 {
		return nil, errors.New("invalid number of x and o")
	}
	g.board = runes
	g.turnCtr = num1 + num2
	g.winnerIdx = -1
	return g, nil
}

// start sets turnCtr to 0.
func (g *Game) Start() {
	if g.turnCtr != -1 {
		panic("game already started")
	}
	g.turnCtr = 0
}

func (g *Game) Play(player std.Address, posX, posY int) error {
	if !g.Started() {
		return errors.New("game not started")
	}

	if g.Turn() != player {
		return errors.New("invalid turn")
	}

	if g.IsOver() {
		return errors.New("game over")
	}

	// are posX and posY valid
	if posX < 0 || posY < 0 || posX > 2 || posY > 2 {
		return errors.New("posX and posY should be 0, 1 or 2")
	}

	// is slot already used?
	idx := xyToIdx(posX, posY)
	if g.board[idx] != 0 {
		return ufmt.Errorf("slot already used (%d, %d)", posX, posY)
	}

	// play
	playerVal := rune(g.turnCtr%2) + 1 // player1=1, player2=2
	g.board[idx] = playerVal

	// check if win
	if g.checkLastMoveWon(posX, posY) {
		g.winnerIdx = g.turnCtr
	}

	// change turn
	g.turnCtr++
	return nil
}

func (g Game) WouldWin(side rune, x, y int) bool {
	idx := xyToIdx(x, y)
	if g.board[idx] != rune(0) {
		panic("tile should be empty")
	}
	// place rune temporarily
	g.board[idx] = side
	b := g.checkLastMoveWon(x, y)
	g.board[idx] = rune(0)
	return b
}

func (g Game) checkLastMoveWon(posX, posY int) bool {
	// assumes the game wasn't won yet, and that the move was already applied.

	// check vertical line
	{
		a := g.At(posX, 0)
		b := g.At(posX, 1)
		c := g.At(posX, 2)
		if a == b && b == c {
			return true
		}
	}

	// check horizontal line
	{
		a := g.At(0, posY)
		b := g.At(1, posY)
		c := g.At(2, posY)
		if a == b && b == c {
			return true
		}
	}

	// diagonals
	{
		tl := g.At(0, 0)
		tr := g.At(0, 2)
		bl := g.At(2, 0)
		br := g.At(2, 2)
		c := g.At(1, 1)
		if posX == posY && tl == c && c == br {
			return true
		}
		if posX+posY == 2 && tr == c && c == bl {
			return true
		}
	}
	return false
}

func (g Game) At(posX, posY int) rune { return g.board[xyToIdx(posX, posY)] }
func (g Game) Winner() std.Address    { return g.PlayerByIndex(g.winnerIdx) }
func (g Game) Turn() std.Address      { return g.PlayerByIndex(g.turnCtr) }
func (g Game) TurnNumber() int        { return g.turnCtr }
func (g Game) IsDraw() bool           { return g.turnCtr > 8 && g.winnerIdx == -1 }
func (g Game) Started() bool          { return g.turnCtr >= 0 }

func (g Game) IsOver() bool {
	// draw
	if g.turnCtr > 8 {
		return true
	}

	// winner
	return g.Winner() != std.Address("")
}

func (g Game) Output() string {
	output := ""

	for y := 2; y >= 0; y-- {
		for x := 0; x < 3; x++ {
			val := g.At(x, y)
			switch val {
			case 0:
				output += "-"
			case 1:
				output += "O"
			case 2:
				output += "X"
			}
		}
		output += "\n"
	}

	return output
}

func (g Game) PlayerByIndex(idx int) std.Address {
	switch idx % 2 {
	case 0:
		return g.player1
	case 1:
		return g.player2
	default:
		return std.Address("")
	}
}

func xyToIdx(x, y int) int { return y*3 + x }

package tictactoe1p

// a 1P-vs-CPU tictactoe
// extending moul's tictactoe model

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/grepsuzette/pr2554/v1/tictactoe"
	"gno.land/p/grepsuzette/pr2554/v1/ufmt"
)

// 1P-VS-CPU is a tictactoe game
type Game struct {
	*tictactoe.Game
	cpu      std.Address
	Intn     func(n int) int
	pickMove func(g *tictactoe.Game, Intn func(int) int) (x, y int, e error) // AI function
}

// "" is a whole new game, whether CPU goes first is random.
// "---------"` OTOH indicates a game where cpu /declined/ to go first.
// fRand is a user-supplied func returning a random number in [0, n(
// fPickMove is nil (uses averageDifficulty) or a func used for CPU to pick moves
func GameFromRepr(
	text string,
	cpu, human std.Address,
	fRand func(int) int,
	fPickMove func(*tictactoe.Game, func(int) int,
	) (x, y int, e error),
) (*Game, error) {
	addr1, addr2 := decideOrder(cpu, human, text, fRand)

	var g *tictactoe.Game
	var e error
	switch len(text) {
	case 0:
		g, e = tictactoe.NewGame(addr1, addr2)
		g.Start()
	case 9:
		r := []rune(text)
		g, e = tictactoe.RecoverGame(
			addr1,
			addr2,
			string([]rune{
				r[6], r[7], r[8],
				r[3], r[4], r[5],
				r[0], r[1], r[2],
			}),
		)
	default:
		return nil, errors.New("invalid board length")
	}
	if fPickMove == nil {
		fPickMove = averageDifficulty
	}
	return &Game{g, cpu, fRand, fPickMove}, e
}

func (game Game) ToRepr() string {
	return strings.ReplaceAll(game.Output(), "\n", "")
}

func (game Game) IsCpuFirst() bool {
	return game.PlayerByIndex(0) == game.cpu
}

func (game *Game) PlayCPU() (x, y int, e error) {
	switch {
	case game.Turn() != game.cpu:
		return -1, -1, ufmt.Errorf(
			"not my turn (%s), turn is %s's",
			game.cpu.String(), game.Turn().String(),
		)
	case game.IsOver():
		return -1, -1, errors.New("game is over")
	default:
		x, y, _ = game.pickMove(game.Game, game.Intn)
		e = game.Play(game.cpu, x, y)
		return x, y, e
	}
}

// Decide who go first, based on the count of markers.
// No error. A special and important case is the empty board,
// meaning player to go first is random.
// fRand is a user-supplied func returning a random number in [0, n(
func decideOrder(cpu, human std.Address, board string, fRand func(int) int) (addr1, addr2 std.Address) {
	var cpuFirst bool
	if board == "" {
		cpuFirst = fRand(2) == 0
	} else {
		numO := strings.Count(board, "O") + strings.Count(board, "o")
		numX := strings.Count(board, "X") + strings.Count(board, "x")
		cpuFirst = numX != numO
	}
	if cpuFirst {
		return cpu, human
	} else {
		return human, cpu
	}
}

package tictactoe1p

import (
	"errors"

	"gno.land/p/grepsuzette/pr2554/v1/tictactoe"
)

// the default move picker.
func averageDifficulty(game *tictactoe.Game, Intn func(int) int) (x, y int, e error) {
	// candidates
	a := make([]struct{ x, y int }, 0)
	side := rune(1 + game.TurnNumber()%2)
	for y := 0; y <= 2; y++ {
		for x := 0; x <= 2; x++ {
			if game.At(x, y) != rune(0) {
				continue
			}
			// if can wins (and can see it), then win
			if game.WouldWin(side, x, y) && Intn(5) > 0 {
				return x, y, nil
			}
			a = append(a, struct{ x, y int }{x, y})
		}
	}
	if len(a) == 0 {
		return -1, -1, errors.New("no free tile left")
	} else {
		// random pick among candidates
		c := a[Intn(len(a))]
		return c.x, c.y, nil
	}
}

