package domain

import (
	"time"
)

// Trait represents a key-value pair with an optional display type for metadata attributes
type Trait struct {
	DisplayType string // Optional display type (e.g., "date", "number", etc.)
	TraitType   string // Type of the trait (e.g., "age", "height", etc.)
	Value       string // Value of the trait
}

// Metadata represents the metadata associated with a domain
type Metadata struct {
	Avatar           string    // URL or identifier for an avatar image
	RegistrationTime time.Time // The time when the domain was registered
	ExpirationTime   time.Time // The time when the domain will be expire
	Attributes       []Trait   // Additional attributes of the domain
	Description      string    // A description of the domain
	ContactInfo      string    // Contact information for the domain owner
	RenewalFee       string    // The fee required to renew the domain, represented as a string
}

// NewMetadata creates a new Metadata instance
func NewMetadata(avatar, description, contactInfo, renewalFee string,
	registrationTime, expirationTime time.Time, attributes []Trait,
) Metadata {
	return Metadata{
		Avatar:           avatar,
		RegistrationTime: registrationTime,
		ExpirationTime:   expirationTime,
		RenewalFee:       renewalFee,
		Attributes:       attributes,
		Description:      description,
		ContactInfo:      contactInfo,
	}
}

package domain

import (
	"std"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/grc/grc721"
)

// domainRegistry represents a registry for domain names with metadata
type domainRegistry struct {
	domains  grc721.IGRC721 // Interface for basic NFT functionality
	metadata *avl.Tree      // AVL tree for storing domain metadata
	expDate  time.Time
}

// DomainRegistry defines the methods for managing domain names and metadata
type DomainRegistry interface {
	BalanceOf(owner std.Address) (uint64, error)
	OwnerOf(domainName string) (std.Address, error)
	SafeTransferFrom(from, to std.Address, domainName string) error
	TransferFrom(from, to std.Address, domainName string) error
	Approve(approved std.Address, domainName string) error
	SetApprovalForAll(operator std.Address, approved bool) error
	GetApproved(domainName string) (std.Address, error)
	IsApprovedForAll(owner, operator std.Address) bool
	Mint(to std.Address, domainName string) error

	RegisterDomain(owner std.Address, domainName string, metadata Metadata, dur time.Duration) error
	SetDomainData(domainName string, metadata Metadata) error
	GetDomainData(domainName string, field MetadataField) (Metadata, error)
	GetDomainFields(domainName string, fields []MetadataField) (Metadata, error)
	RenewDomain(domainName string, additionalDuration time.Duration) error
	GetExpirationDate(domainName string) time.Time
	SetExpirationDate(domainName string, expDate time.Time) bool
}

// NewDomainRegistry creates a new domain registry with metadata extensions
func NewDomainRegistry(name, symbol string) *domainRegistry {
	registry := grc721.NewBasicNFT(name, symbol)

	return &domainRegistry{
		domains:  registry,
		metadata: avl.NewTree(),
	}
}

// RegisterDomain registers a new domain with the given metadata
func (d *domainRegistry) RegisterDomain(owner std.Address, domainName string, metadata Metadata, dur time.Duration) error {
	err := d.domains.Mint(owner, grc721.TokenID(domainName))
	if err != nil {
		return err
	}
	d.expDate = time.Now().Add(dur)
	d.metadata.Set(domainName, metadata)

	return nil
}

// RenewDomain extends the expiration time of a domain name
func (d *domainRegistry) RenewDomain(domainName string, additionalDuration time.Duration) error {
	_, found := d.metadata.Get(domainName)
	if !found {
		return ErrInvalidDomainName
	}

	owner, err := d.domains.OwnerOf(grc721.TokenID(domainName))
	if err != nil {
		return err
	}

	caller := std.PrevRealm().Addr()
	if caller != owner {
		return ErrUnauthorized
	}

	// set new expiration date
	d.expDate = d.expDate.Add(additionalDuration)
	return nil
}

// SetDomainData sets the metadata for a given domain name
func (d *domainRegistry) SetDomainData(domainName string, metadata Metadata) error {
	owner, err := d.domains.OwnerOf(grc721.TokenID(domainName))
	if err != nil {
		return err
	}

	caller := std.PrevRealm().Addr()
	if caller != owner {
		return ErrUnauthorized
	}

	d.metadata.Set(domainName, metadata)
	return nil
}

// GetDomainFields retrieves multiple fields of metadata for a given domain
func (d *domainRegistry) GetDomainFields(domainName string, fields []MetadataField) (Metadata, error) {
	data, found := d.metadata.Get(domainName)
	if !found {
		return Metadata{}, ErrInvalidDomainName
	}

	metadata := data.(Metadata)

	if len(fields) == 0 {
		return metadata, nil
	}

	var result Metadata
	for _, field := range fields {
		switch field {
		case FieldAvatar:
			result.Avatar = metadata.Avatar
		case FieldRegistrationTime:
			result.RegistrationTime = metadata.RegistrationTime
		case FieldExpirationTime:
			result.ExpirationTime = metadata.ExpirationTime
		case FieldRenewalFee:
			result.RenewalFee = metadata.RenewalFee
		case FieldAttributes:
			result.Attributes = metadata.Attributes
		case FieldDescription:
			result.Description = metadata.Description
		case FieldContactInfo:
			result.ContactInfo = metadata.ContactInfo
		default:
			return Metadata{}, ErrInvalidMetadataField
		}
	}

	return result, nil
}

// GetDomainData retrieves metadata for a given domain
func (d *domainRegistry) GetDomainData(domainName string, field MetadataField) (Metadata, error) {
	data, found := d.metadata.Get(domainName)
	if !found {
		return Metadata{}, ErrInvalidDomainName
	}

	metadata := data.(Metadata)

	switch field {
	case FieldAvatar:
		return Metadata{
			Avatar: metadata.Avatar,
		}, nil
	case FieldRegistrationTime:
		return Metadata{
			RegistrationTime: metadata.RegistrationTime,
		}, nil
	case FieldExpirationTime:
		return Metadata{
			ExpirationTime: metadata.ExpirationTime,
		}, nil
	case FieldRenewalFee:
		return Metadata{
			RenewalFee: metadata.RenewalFee,
		}, nil
	case FieldAttributes:
		return Metadata{
			Attributes: metadata.Attributes,
		}, nil
	case FieldDescription:
		return Metadata{
			Description: metadata.Description,
		}, nil
	case FieldContactInfo:
		return Metadata{
			ContactInfo: metadata.ContactInfo,
		}, nil
	default:
		return Metadata{}, ErrInvalidMetadataField
	}
}

// BalanceOf returns the number of domains owned by a given address
func (d *domainRegistry) BalanceOf(owner std.Address) (uint64, error) {
	return d.domains.BalanceOf(owner)
}

// OwnerOf returns the owner of a given domain name
func (d *domainRegistry) OwnerOf(domainName string) (std.Address, error) {
	return d.domains.OwnerOf(grc721.TokenID(domainName))
}

// SafeTransferFrom safely transfers a domain from one address to another
func (d *domainRegistry) SafeTransferFrom(from, to std.Address, domainName string) error {
	return d.domains.SafeTransferFrom(from, to, grc721.TokenID(domainName))
}

// TransferFrom transfers a domain from one address to another
func (d *domainRegistry) TransferFrom(from, to std.Address, domainName string) error {
	return d.domains.TransferFrom(from, to, grc721.TokenID(domainName))
}

// Approve grants approval to another address to manage a specific domain
func (d *domainRegistry) Approve(approved std.Address, domainName string) error {
	return d.domains.Approve(approved, grc721.TokenID(domainName))
}

// SetApprovalForAll sets approval for an operator to manage all domains of the owner
func (d *domainRegistry) SetApprovalForAll(operator std.Address, approved bool) error {
	return d.domains.SetApprovalForAll(operator, approved)
}

// GetApproved returns the approved address for a specific domain
func (d *domainRegistry) GetApproved(domainName string) (std.Address, error) {
	return d.domains.GetApproved(grc721.TokenID(domainName))
}

// IsApprovedForAll checks if an operator is approved to manage all domains of the owner
func (d *domainRegistry) IsApprovedForAll(owner, operator std.Address) bool {
	return d.domains.IsApprovedForAll(owner, operator)
}

// Mint creates a new domain for a given address
func (d *domainRegistry) Mint(to std.Address, domainName string) error {
	return d.domains.Mint(to, grc721.TokenID(domainName))
}

func (d *domainRegistry) GetExpirationDate(domainName string) time.Time {
	return d.expDate
}

func (d *domainRegistry) SetExpirationDate(domainName string, expDate time.Time) bool {
	_, found := d.metadata.Get(domainName)
	if !found {
		return false
	}
	d.expDate = expDate
	return true
}

package domain

import (
	"errors"
)

var (
	ErrUnauthorized         = errors.New("caller is not domain owner")
	ErrInvalidDomainName    = errors.New("invalid domain name")
	ErrInvalidMetadataField = errors.New("invalid metadata field")
	ErrInsufficientFunds    = errors.New("insufficient funds for renewal")
)

package grc721

import (
	"std"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/ufmt"
)

type basicNFT struct {
	name              string
	symbol            string
	owners            avl.Tree // tokenId -> OwnerAddress
	balances          avl.Tree // OwnerAddress -> TokenCount
	tokenApprovals    avl.Tree // TokenId -> ApprovedAddress
	tokenURIs         avl.Tree // TokenId -> URIs
	operatorApprovals avl.Tree // "OwnerAddress:OperatorAddress" -> bool
}

// Returns new basic NFT
func NewBasicNFT(name string, symbol string) *basicNFT {
	return &basicNFT{
		name:   name,
		symbol: symbol,

		owners:            avl.Tree{},
		balances:          avl.Tree{},
		tokenApprovals:    avl.Tree{},
		tokenURIs:         avl.Tree{},
		operatorApprovals: avl.Tree{},
	}
}

func (s *basicNFT) Name() string       { return s.name }
func (s *basicNFT) Symbol() string     { return s.symbol }
func (s *basicNFT) TokenCount() uint64 { return uint64(s.owners.Size()) }

// BalanceOf returns balance of input address
func (s *basicNFT) BalanceOf(addr std.Address) (uint64, error) {
	if err := isValidAddress(addr); err != nil {
		return 0, err
	}

	balance, found := s.balances.Get(addr.String())
	if !found {
		return 0, nil
	}

	return balance.(uint64), nil
}

// OwnerOf returns owner of input token id
func (s *basicNFT) OwnerOf(tid TokenID) (std.Address, error) {
	owner, found := s.owners.Get(string(tid))
	if !found {
		return "", ErrInvalidTokenId
	}

	return owner.(std.Address), nil
}

// TokenURI returns the URI of input token id
func (s *basicNFT) TokenURI(tid TokenID) (string, error) {
	uri, found := s.tokenURIs.Get(string(tid))
	if !found {
		return "", ErrInvalidTokenId
	}

	return uri.(string), nil
}

func (s *basicNFT) SetTokenURI(tid TokenID, tURI TokenURI) (bool, error) {
	// check for invalid TokenID
	if !s.exists(tid) {
		return false, ErrInvalidTokenId
	}

	// check for the right owner
	owner, err := s.OwnerOf(tid)
	if err != nil {
		return false, err
	}
	caller := std.PrevRealm().Addr()
	if caller != owner {
		return false, ErrCallerIsNotOwner
	}
	s.tokenURIs.Set(string(tid), string(tURI))
	return true, nil
}

// IsApprovedForAll returns true if operator is approved for all by the owner.
// Otherwise, returns false
func (s *basicNFT) IsApprovedForAll(owner, operator std.Address) bool {
	key := owner.String() + ":" + operator.String()
	_, found := s.operatorApprovals.Get(key)
	if !found {
		return false
	}

	return true
}

// Approve approves the input address for particular token
func (s *basicNFT) Approve(to std.Address, tid TokenID) error {
	if err := isValidAddress(to); err != nil {
		return err
	}

	owner, err := s.OwnerOf(tid)
	if err != nil {
		return err
	}
	if owner == to {
		return ErrApprovalToCurrentOwner
	}

	caller := std.PrevRealm().Addr()
	if caller != owner && !s.IsApprovedForAll(owner, caller) {
		return ErrCallerIsNotOwnerOrApproved
	}

	s.tokenApprovals.Set(string(tid), to.String())
	event := ApprovalEvent{owner, to, tid}
	emit(&event)

	return nil
}

// GetApproved return the approved address for token
func (s *basicNFT) GetApproved(tid TokenID) (std.Address, error) {
	addr, found := s.tokenApprovals.Get(string(tid))
	if !found {
		return zeroAddress, ErrTokenIdNotHasApproved
	}

	return std.Address(addr.(string)), nil
}

// SetApprovalForAll can approve the operator to operate on all tokens
func (s *basicNFT) SetApprovalForAll(operator std.Address, approved bool) error {
	if err := isValidAddress(operator); err != nil {
		return ErrInvalidAddress
	}

	caller := std.PrevRealm().Addr()
	return s.setApprovalForAll(caller, operator, approved)
}

// Safely transfers `tokenId` token from `from` to `to`, checking that
// contract recipients are aware of the GRC721 protocol to prevent
// tokens from being forever locked.
func (s *basicNFT) SafeTransferFrom(from, to std.Address, tid TokenID) error {
	caller := std.PrevRealm().Addr()
	if !s.isApprovedOrOwner(caller, tid) {
		return ErrCallerIsNotOwnerOrApproved
	}

	err := s.transfer(from, to, tid)
	if err != nil {
		return err
	}

	if !s.checkOnGRC721Received(from, to, tid) {
		return ErrTransferToNonGRC721Receiver
	}

	return nil
}

// Transfers `tokenId` token from `from` to `to`.
func (s *basicNFT) TransferFrom(from, to std.Address, tid TokenID) error {
	caller := std.PrevRealm().Addr()
	if !s.isApprovedOrOwner(caller, tid) {
		return ErrCallerIsNotOwnerOrApproved
	}

	err := s.transfer(from, to, tid)
	if err != nil {
		return err
	}

	return nil
}

// Mints `tokenId` and transfers it to `to`.
func (s *basicNFT) Mint(to std.Address, tid TokenID) error {
	return s.mint(to, tid)
}

// Mints `tokenId` and transfers it to `to`. Also checks that
// contract recipients are using GRC721 protocol
func (s *basicNFT) SafeMint(to std.Address, tid TokenID) error {
	err := s.mint(to, tid)
	if err != nil {
		return err
	}

	if !s.checkOnGRC721Received(zeroAddress, to, tid) {
		return ErrTransferToNonGRC721Receiver
	}

	return nil
}

func (s *basicNFT) Burn(tid TokenID) error {
	owner, err := s.OwnerOf(tid)
	if err != nil {
		return err
	}

	s.beforeTokenTransfer(owner, zeroAddress, tid, 1)

	s.tokenApprovals.Remove(string(tid))
	balance, err := s.BalanceOf(owner)
	if err != nil {
		return err
	}
	balance -= 1
	s.balances.Set(owner.String(), balance)
	s.owners.Remove(string(tid))

	event := TransferEvent{owner, zeroAddress, tid}
	emit(&event)

	s.afterTokenTransfer(owner, zeroAddress, tid, 1)

	return nil
}

/* Helper methods */

// Helper for SetApprovalForAll()
func (s *basicNFT) setApprovalForAll(owner, operator std.Address, approved bool) error {
	if owner == operator {
		return ErrApprovalToCurrentOwner
	}

	key := owner.String() + ":" + operator.String()
	s.operatorApprovals.Set(key, approved)

	event := ApprovalForAllEvent{owner, operator, approved}
	emit(&event)

	return nil
}

// Helper for TransferFrom() and SafeTransferFrom()
func (s *basicNFT) transfer(from, to std.Address, tid TokenID) error {
	if err := isValidAddress(from); err != nil {
		return ErrInvalidAddress
	}
	if err := isValidAddress(to); err != nil {
		return ErrInvalidAddress
	}

	if from == to {
		return ErrCannotTransferToSelf
	}

	owner, err := s.OwnerOf(tid)
	if err != nil {
		return err
	}
	if owner != from {
		return ErrTransferFromIncorrectOwner
	}

	s.beforeTokenTransfer(from, to, tid, 1)

	// Check that tokenId was not transferred by `beforeTokenTransfer`
	owner, err = s.OwnerOf(tid)
	if err != nil {
		return err
	}
	if owner != from {
		return ErrTransferFromIncorrectOwner
	}

	s.tokenApprovals.Remove(string(tid))
	fromBalance, err := s.BalanceOf(from)
	if err != nil {
		return err
	}
	toBalance, err := s.BalanceOf(to)
	if err != nil {
		return err
	}
	fromBalance -= 1
	toBalance += 1
	s.balances.Set(from.String(), fromBalance)
	s.balances.Set(to.String(), toBalance)
	s.owners.Set(string(tid), to)

	event := TransferEvent{from, to, tid}
	emit(&event)

	s.afterTokenTransfer(from, to, tid, 1)

	return nil
}

// Helper for Mint() and SafeMint()
func (s *basicNFT) mint(to std.Address, tid TokenID) error {
	if err := isValidAddress(to); err != nil {
		return err
	}

	if s.exists(tid) {
		return ErrTokenIdAlreadyExists
	}

	s.beforeTokenTransfer(zeroAddress, to, tid, 1)

	// Check that tokenId was not minted by `beforeTokenTransfer`
	if s.exists(tid) {
		return ErrTokenIdAlreadyExists
	}

	toBalance, err := s.BalanceOf(to)
	if err != nil {
		return err
	}
	toBalance += 1
	s.balances.Set(to.String(), toBalance)
	s.owners.Set(string(tid), to)

	event := TransferEvent{zeroAddress, to, tid}
	emit(&event)

	s.afterTokenTransfer(zeroAddress, to, tid, 1)

	return nil
}

func (s *basicNFT) isApprovedOrOwner(addr std.Address, tid TokenID) bool {
	owner, found := s.owners.Get(string(tid))
	if !found {
		return false
	}

	if addr == owner.(std.Address) || s.IsApprovedForAll(owner.(std.Address), addr) {
		return true
	}

	_, err := s.GetApproved(tid)
	if err != nil {
		return false
	}

	return true
}

// Checks if token id already exists
func (s *basicNFT) exists(tid TokenID) bool {
	_, found := s.owners.Get(string(tid))
	return found
}

func (s *basicNFT) beforeTokenTransfer(from, to std.Address, firstTokenId TokenID, batchSize uint64) {
	// TODO: Implementation
}

func (s *basicNFT) afterTokenTransfer(from, to std.Address, firstTokenId TokenID, batchSize uint64) {
	// TODO: Implementation
}

func (s *basicNFT) checkOnGRC721Received(from, to std.Address, tid TokenID) bool {
	// TODO: Implementation
	return true
}

func (s *basicNFT) RenderHome() (str string) {
	str += ufmt.Sprintf("# %s ($%s)\n\n", s.name, s.symbol)
	str += ufmt.Sprintf("* **Total supply**: %d\n", s.TokenCount())
	str += ufmt.Sprintf("* **Known accounts**: %d\n", s.balances.Size())

	return
}

package grc721

import (
	"std"
	"testing"

	"gno.land/p/demo/testutils"
	"gno.land/p/demo/uassert"
)

func TestSetMetadata(t *testing.T) {
	// Create a new dummy NFT with metadata
	dummy := NewNFTWithMetadata(dummyNFTName, dummyNFTSymbol)
	if dummy == nil {
		t.Errorf("should not be nil")
	}

	// Define addresses for testing purposes
	addr1 := testutils.TestAddress("alice")
	addr2 := testutils.TestAddress("bob")

	// Define metadata attributes
	name := "test"
	description := "test"
	image := "test"
	imageData := "test"
	externalURL := "test"
	attributes := []Trait{}
	backgroundColor := "test"
	animationURL := "test"
	youtubeURL := "test"

	// Set the original caller to addr1
	std.TestSetOrigCaller(addr1) // addr1

	// Mint a new token for addr1
	dummy.mint(addr1, TokenID("1"))

	// Set metadata for token 1
	derr := dummy.SetTokenMetadata(TokenID("1"), Metadata{
		Name:            name,
		Description:     description,
		Image:           image,
		ImageData:       imageData,
		ExternalURL:     externalURL,
		Attributes:      attributes,
		BackgroundColor: backgroundColor,
		AnimationURL:    animationURL,
		YoutubeURL:      youtubeURL,
	})

	// Check if there was an error setting metadata
	uassert.NoError(t, derr, "Should not result in error")

	// Test case: Invalid token ID
	err := dummy.SetTokenMetadata(TokenID("3"), Metadata{
		Name:            name,
		Description:     description,
		Image:           image,
		ImageData:       imageData,
		ExternalURL:     externalURL,
		Attributes:      attributes,
		BackgroundColor: backgroundColor,
		AnimationURL:    animationURL,
		YoutubeURL:      youtubeURL,
	})

	// Check if the error returned matches the expected error
	uassert.ErrorIs(t, err, ErrInvalidTokenId)

	// Set the original caller to addr2
	std.TestSetOrigCaller(addr2) // addr2

	// Try to set metadata for token 1 from addr2 (should fail)
	cerr := dummy.SetTokenMetadata(TokenID("1"), Metadata{
		Name:            name,
		Description:     description,
		Image:           image,
		ImageData:       imageData,
		ExternalURL:     externalURL,
		Attributes:      attributes,
		BackgroundColor: backgroundColor,
		AnimationURL:    animationURL,
		YoutubeURL:      youtubeURL,
	})

	// Check if the error returned matches the expected error
	uassert.ErrorIs(t, cerr, ErrCallerIsNotOwner)

	// Set the original caller back to addr1
	std.TestSetOrigCaller(addr1) // addr1

	// Retrieve metadata for token 1
	dummyMetadata, err := dummy.TokenMetadata(TokenID("1"))
	uassert.NoError(t, err, "Metadata error")

	// Check if metadata attributes match expected values
	uassert.Equal(t, image, dummyMetadata.Image)
	uassert.Equal(t, imageData, dummyMetadata.ImageData)
	uassert.Equal(t, externalURL, dummyMetadata.ExternalURL)
	uassert.Equal(t, description, dummyMetadata.Description)
	uassert.Equal(t, name, dummyMetadata.Name)
	uassert.Equal(t, len(attributes), len(dummyMetadata.Attributes))
	uassert.Equal(t, backgroundColor, dummyMetadata.BackgroundColor)
	uassert.Equal(t, animationURL, dummyMetadata.AnimationURL)
	uassert.Equal(t, youtubeURL, dummyMetadata.YoutubeURL)
}

package grc721

import (
	"std"

	"gno.land/p/demo/avl"
)

// metadataNFT represents an NFT with metadata extensions.
type metadataNFT struct {
	*basicNFT            // Embedded basicNFT struct for basic NFT functionality
	extensions *avl.Tree // AVL tree for storing metadata extensions
}

// Ensure that metadataNFT implements the IGRC721MetadataOnchain interface.
var _ IGRC721MetadataOnchain = (*metadataNFT)(nil)

// NewNFTWithMetadata creates a new basic NFT with metadata extensions.
func NewNFTWithMetadata(name string, symbol string) *metadataNFT {
	// Create a new basic NFT
	nft := NewBasicNFT(name, symbol)

	// Return a metadataNFT with basicNFT embedded and an empty AVL tree for extensions
	return &metadataNFT{
		basicNFT:   nft,
		extensions: avl.NewTree(),
	}
}

// SetTokenMetadata sets metadata for a given token ID.
func (s *metadataNFT) SetTokenMetadata(tid TokenID, metadata Metadata) error {
	// Check if the caller is the owner of the token
	owner, err := s.basicNFT.OwnerOf(tid)
	if err != nil {
		return err
	}
	caller := std.PrevRealm().Addr()
	if caller != owner {
		return ErrCallerIsNotOwner
	}

	// Set the metadata for the token ID in the extensions AVL tree
	s.extensions.Set(string(tid), metadata)
	return nil
}

// TokenMetadata retrieves metadata for a given token ID.
func (s *metadataNFT) TokenMetadata(tid TokenID) (Metadata, error) {
	// Retrieve metadata from the extensions AVL tree
	metadata, found := s.extensions.Get(string(tid))
	if !found {
		return Metadata{}, ErrInvalidTokenId
	}

	return metadata.(Metadata), nil
}

// mint mints a new token and assigns it to the specified address.
func (s *metadataNFT) mint(to std.Address, tid TokenID) error {
	// Check if the address is valid
	if err := isValidAddress(to); err != nil {
		return err
	}

	// Check if the token ID already exists
	if s.basicNFT.exists(tid) {
		return ErrTokenIdAlreadyExists
	}

	s.basicNFT.beforeTokenTransfer(zeroAddress, to, tid, 1)

	// Check if the token ID was minted by beforeTokenTransfer
	if s.basicNFT.exists(tid) {
		return ErrTokenIdAlreadyExists
	}

	// Increment balance of the recipient address
	toBalance, err := s.basicNFT.BalanceOf(to)
	if err != nil {
		return err
	}
	toBalance += 1
	s.basicNFT.balances.Set(to.String(), toBalance)

	// Set owner of the token ID to the recipient address
	s.basicNFT.owners.Set(string(tid), to)

	// Emit transfer event
	event := TransferEvent{zeroAddress, to, tid}
	emit(&event)

	s.basicNFT.afterTokenTransfer(zeroAddress, to, tid, 1)

	return nil
}

