	if err := g.play(std.PrevRealm().Addr(), roll); err != nil {
		panic(err)
	}

	// If both players have rolled, update the results and leaderboard
	if g.roll1 != 0 && g.roll2 != 0 {
		// If the player is playing against themselves, no points are awarded
