package tictactoe

// Stateless human VS CPU Tic-tac-toe
// Markdown + HTML1.0 + gnolang
// no javascript, no wallet needed.

import (
	"math/rand"
	"std"
	"strings"

	"gno.land/p/grepsuzette/pr2554/v1/ternary"
	"gno.land/p/grepsuzette/pr2554/v1/tictactoe/tictactoe1p"
	"gno.land/p/grepsuzette/pr2554/v1/ufmt"
)

const (
	cpuAddress   = std.Address("gCPU")
	humanAddress = std.Address("gHUMAN")
	urlParrot    = "https://raw.githubusercontent.com/grepsuzette/gfx/master/parrot.png"
	statusWon    = "Looks like you've won!"
	statusLost   = "Sorry mate, you lost!"
	statusDraw   = "It's a draw..."
)

var prng *rand.Rand

const rRoot = "/r/grepsuzette/pr2554/v3"

func Intn(n int) int {
	if prng == nil {
		// Note: our PRNG is not stateful as calling Render is not going
		// to modify this stateless realm. We initialize it here when still nil
		// this creates the randomness we need (seeded from blockchain's height)
		prng = rand.New(rand.NewPCG(uint64(std.GetHeight()), uint64(9)))
	}
	return prng.IntN(n)
}

func Render(path string) string {
	game, x, y, debug := parsePath(path)
	if x != -1 && y != -1 {
		game.Play(humanAddress, x, y)
	}
	cpuX, cpuY, e := game.PlayCPU()
	output := parrotTalk(*game)
	output += render(*game, cpuX, cpuY)
	if debug {
		output += ufmt.Sprintf(
			"--- played x=%d y=%d cpuX=%d cpuY=%d height=%d path=%s turn:%d error:%s",
			x, y, cpuX, cpuY, std.GetHeight(), path, game.Game.TurnNumber(), e,
		)
	}
	return output
}

// Lower-case render is simply called by Render.
// Note the <form> (below) could be a <div>. If some day
// realms can access GET and POST variables, then regular
// <button> can be used. <form> is kept to remember that.
func render(game tictactoe1p.Game, cpuX, cpuY int) string {
	status, class := statusAndClass(game)
	repr := game.ToRepr()
	output := css()
	output += `<form id="board" class="` + class + `">` + "\n"
	// output += "\t" + `<input type="hidden" name="state" value="` + repr + `">` + "\n"
	output += `<div id="left"><img src="` + urlParrot + `" id="parrot" width="120" height="120" align="left" /></div>` + "\n"
	output += `<div id="right">`

	for y := 2; y >= 0; y-- {
		output += "\t"
		for x := 0; x <= 2; x++ {
			runeAtXY := game.At(x, y)
			occupied := runeAtXY != rune(0)
			if occupied {
				highlighted := x == cpuX && y == cpuY
				output += button(x, y, ternary.String(runeAtXY == rune(1), "O", "X"), true, highlighted, repr)
			} else if game.IsOver() {
				output += button(x, y, "&nbsp;", true, false, repr)
			} else {
				// tile is free, but :hover must show
				cpu1st := game.Game.PlayerByIndex(0) == cpuAddress
				output += button(x, y, ternary.String(cpu1st, "X", "O"), false, false, repr)
			}
		}
		output += "<br />\n"
	}
	output += "</div>\n"
	output += "</form>\n"
	if game.IsOver() {
		output += ufmt.Sprintf(
			"\n%s [ %s | %s ]\n",
			status,
			"[New game]("+rRoot+"/games/tictactoe)",
			"[Back to demo/games]("+rRoot+"/games)",
		)
	} else {
		output += "\n[Okay country roads, take me home](" + rRoot + "/games)"
	}
	return output
}

func button(x, y int, char string, occupied, highlighted bool, repr string) string {
	return ufmt.Sprintf(
		`<a href="`+rRoot+`/games/tictactoe:state=%s&move=%c%d" class="button tile %s %s">%s</a>`,
		repr,
		rune('a'+x), y+1,
		ternary.String(occupied, "disabled", ""),
		ternary.String(highlighted, "highlighted", ""),
		char,
	)
}

// return status and class(es).
// it's empty when the game is not over.
func statusAndClass(game tictactoe1p.Game) (status string, classes string) {
	if game.IsOver() {
		switch {
		case game.Winner() == humanAddress:
			classes = "over won"
			status = statusWon
		case game.Winner() == cpuAddress:
			classes = "over lost"
			status = statusLost
		default:
			classes = "over draw"
			status = statusDraw
		}
	}
	return
}

// Decompose path, into valid game and played coordinates (or -1,-1)
// The path is like "state=O--X-----&move=c3"
//
//   - state is empty or 9 characters,
//     imagine the following but without the \n: "-OX\nO--\n---".
//     O always plays first.
//
//   - move coordinate ranges from "a1" to "c3" but can be empty.
//
//   - debug &debug enables printing of a debug line.
func parsePath(path string) (game *tictactoe1p.Game, x, y int, debug bool) {
	h, e := BreakToMap(path)
	if e != nil {
		panic(e)
	}
	// nil: default AI
	game, e = tictactoe1p.GameFromRepr(h["state"], cpuAddress, humanAddress, Intn, nil)
	if e != nil {
		panic(e)
	}
	_, debug = h["debug"]
	x, y = -1, -1
	for i, xx := range h["move"] {
		switch i {
		case 0:
			x = int(xx) - int('a')
		case 1:
			y = int(xx) - int('1')
		default:
			panic("invalid move: " + h["move"])
		}
	}
	return game, x, y, debug
}

func parrotTalk(game tictactoe1p.Game) string {
	var a []string
	switch {
	case !game.IsOver():
		a = []string{
			"Let's play Tic-tac-toe!",
			"Cap'n Cluck, am here and ready to rule the high seas of tic-tac-toe!",
			"Aarrr, we'll see who'll be top parrot on this jolly board.",
			"The stakes are higher than the mast of a sunken galleon!",
		}
	case game.IsDraw():
		a = []string{
			"Three cheers for Yers Truly, Cap'n! It's a draw.",
			"It's a draw, sailor...",
			"This match ends in neither victory nor defeat, but a draw!",
		}
	case game.Winner().String() == cpuAddress.String():
		a = []string{
			"Alrighty, mateys! Who's the bravest birdy of the seven seas!",
			"Avast, ye scallywags, for I won this game",
			"Remember, me hearties, even in victory, Cap'n Cluck remains a humble scallywag.",
			"Aarrr, parrot power prevails again!",
			"Cap'n Cluck claims the prize!",
			"The high seas crown me king o' the game!",
		}
	case game.Winner().String() != cpuAddress.String():
		a = []string{
			"Ye bested a bold parrot on this day!",
			"Cap'n Cluck, beaten by a buccaneer? Nay, 'tis unbirdable!",
			"Aarrr, this be the day I, Cap'n Cluck, ate me parroted pirate's words! But fear not, for I shall be back!",
			"Ye bested this parrot, but amongst feathery fiends, revenge is ripe!",
			"Despite this defeat, remember, I, Cap'n Cluck, am not a chicken when it comes to tic-tac-toe!",
		}
	}
	s := a[Intn(len(a))]
	return ufmt.Sprintf("<div><b>%s</b><br />"+strings.Repeat("&nbsp;", 17)+"/</div>", s)
}

func css() string {
	return `
<style type="text/css">
	/* responsive stuffs */
	#board { 
		display: flex; 
		flex-direction: row;
	}
	#board > #left {
		flex-shrink: 1;
	}
	#board > #right {
		flex-shrink: 0;
		flex-basis: auto;
		width: 9.5em;
	}
	/* buttons, and parrot */
	#board a.tile.button { 
	  appearance: button;
	  box-sizing: border-box;
	  margin: 0;
	  font-weight: bold;
	  display: inline-block;
	  background-color: #eee;
	  border-color: rgb(227, 227, 227);
	  border-style: outset;
	  border-width: 1px;
	  border-collapse: separate;
	  text-decoration: none;
	  text-align: center;
	  line-height: 3em;
	}
    #board button, #board a.tile.button { 
        width: 3em; height: 3em; 
        margin-right: 2px; margin-bottom: 2px; 
        cursor: pointer; 
        color: initial;
		border-radius: 5px;
    }
	#board.won button, #board.won a.tile.button  { box-shadow: aquamarine 1px 1px 12px 6px; }
	#board.lost button, #board.lost a.tile.button  { box-shadow: rgb(255, 200, 190) 0px 1px 34px 2px; }
	#board.draw button , #board.draw a.tile.button { box-shadow: rgb(200, 200, 200) 0px 1px 30px 8px; }
	#board button , #board a.tile.button { color: #888; }
	#board button:not([disabled]):hover, #board a.tile.button:not(.disabled):hover { 
        border-style: ridge; 
        box-shadow: inset -10px -10px 15px rgba(255, 255, 255, 0.5), 
                    inset 10px 10px 15px rgba(70, 70, 70, 0.12);
		border-radius: 5px;
    }
    #board button[disabled], #board a.tile.button.disabled { cursor: default; }
	#board button:not([disabled]), #board a.tile.button:not(.disabled) { color: transparent; }
	#board button:not([disabled]):hover , #board a.tile.button:not(.disabled):hover { color: #888; text-transform: uppercase; }
	#board button.highlighted, #board a.tile.button.highlighted { color: chocolate; }
    img#parrot {
        -webkit-transform: scaleX(-1);
        transform: scaleX(-1);
        padding-left: 10px; 
		margin-right: 0.5em;
    }
</style>
`
}

// Break down a string of url parameters to map[string]string.
// E.g. "foo=a&bar=b" -> (map[string]string{foo:"a", bar:"b"}, nil)
func BreakToMap(querystring string) (map[string]string, error) {
	m := make(map[string]string)
	for _, s := range strings.Split(querystring, "&") {
		if len(strings.TrimSpace(s)) == 0 {
			continue
		}
		idx := strings.Index(s, "=")
		if idx > -1 {
			m[s[:idx]] = s[idx+1:]
		} else {
			m[s] = ""
		}
	}
	return m, nil
}

package minesweeper

import (
	"encoding/hex"
	"net/url"
	"strings"
)

const (
	Width  = 8
	Height = 8
	// Mines  = 4 They are random, from 4 (easy) to 10 (difficult) !
)

type Cell struct {
	IsMine        bool
	IsRevealed    bool
	AdjacentMines int
}

type Board struct {
	cells [][]Cell // height, width
	// todo probably add fields width and height
}

func NewBoard() *Board {
	var cells [][]Cell = make([][]Cell, Height)
	for i := range cells {
		cells[i] = make([]Cell, Width)
	}
	return &Board{
		cells: cells,
	}
}

// An empty hexstate will create a board and place mines.
// Otherwise a board is recovered from the state if possible (panic otherwise).
// input is a hex string where each hex-decoded byte 8 bits is 0b00ZYXXXX
//
//	XXXX: count of adjacent mines (4bits, valid ‚àà 0-8),
//	Y: discovered bit
//	Z: isMine bit
func BoardFromRepr(hexstate string) *Board {
	state := stateToBytes(hexstate)
	board := NewBoard()
	if len(state) == 0 {
		board.placeMines()
	} else if len(state) != Width*Height {
		panic("invalid state length")
	} else {
		for i, o := range state {
			board.cells[i/Width][i%Width] = Cell{
				IsMine:        ((o & 0b_0010_0000) >> 5) == 1,
				IsRevealed:    ((o & 0b_0001_0000) >> 4) == 1,
				AdjacentMines: int(o & 0b_0000_1111),
			}
		}
	}
	return board
}

// ToHexRepr is the opposite operation of BoardFromRepr
// it outputs an hexadecimal string
func (b Board) ToHexRepr() string {
	var builder strings.Builder
	for i := 0; i < Width*Height; i++ {
		cell := b.cells[i/Width][i%Width]
		var isMine uint8 = 0
		var isRevealed uint8 = 0
		if cell.IsMine {
			isMine = 1
		}
		if cell.IsRevealed {
			isRevealed = 1
		}
		builder.WriteByte(
			0b_0000_0000 | (isMine << 5) | (isRevealed << 4) | (uint8(cell.AdjacentMines)),
		)
	}
	return hex.EncodeToString([]byte(builder.String()))
}

// show the board as a string,
// respective of the revealed status of the squares unless `force` is true.
func (b *Board) show(force bool) string {
	var buf strings.Builder
	for y := range b.cells {
		for x := range b.cells[y] {
			cell := b.cells[y][x]
			if cell.IsRevealed || force {
				if cell.IsMine {
					buf.WriteRune('+')
				} else if cell.AdjacentMines == 0 {
					buf.WriteRune('.')
				} else {
					buf.WriteRune('0' + int32(cell.AdjacentMines))
				}
			} else {
				buf.WriteRune('?')
			}
		}
		buf.WriteRune('\n')
	}
	return buf.String()
}

// Reveal a square, using some kind of floodfill, and return
// whether we are still alive after that...
func (b *Board) Reveal(x, y int) bool {
	if x < 0 || x >= Width || y < 0 || y >= Height {
		panic("Invalid input. Enter valid coordinates.")
	}
	if b.cells[y][x].IsRevealed || b.cells[y][x].IsMine {
		return !b.cells[y][x].IsMine
	}
	b.cells[y][x].IsRevealed = true
	if b.cells[y][x].AdjacentMines == 0 {
		for i := -1; i <= 1; i++ {
			for j := -1; j <= 1; j++ {
				if i == 0 && j == 0 {
					continue
				}
				adjX, adjY := x+j, y+i
				if adjX >= 0 && adjX < Width && adjY >= 0 && adjY < Height {
					b.Reveal(adjX, adjY)
				}
			}
		}
	}
	return true
}

func (b *Board) placeMines() {
	// Mines vary between 4 (easy) to 10 (relatively difficult)
	mines := 4 + Intn(10-4)
	for i := 0; i < mines; i++ {
		x, y := Intn(Width), Intn(Height)
		if b.cells[y][x].IsMine {
			i-- // Retry if mine is already placed
			continue
		}
		b.cells[y][x].IsMine = true
		b.updateAdjacentMines(x, y)
	}
}

func (b *Board) updateAdjacentMines(x, y int) {
	for i := -1; i <= 1; i++ {
		for j := -1; j <= 1; j++ {
			if i == 0 && j == 0 {
				continue
			}
			adjX, adjY := x+j, y+i
			if adjX >= 0 && adjX < Width && adjY >= 0 && adjY < Height {
				b.cells[adjY][adjX].AdjacentMines++
			}
		}
	}
}

func stateToBytes(urlEncodedHex string) []byte {
	decoded, err := url.QueryUnescape(urlEncodedHex)
	if err != nil {
		panic("Error decoding URL:" + err.Error())
	}
	bytes, err := hex.DecodeString(decoded)
	if err != nil {
		panic("Error decoding hex:" + err.Error())
	}
	return bytes
}

package minesweeper

import (
	"testing"

	"gno.land/p/demo/urequire"
)

func TestBoard(t *testing.T) {
	{
		board := NewBoard()
		urequire.False(t, board == nil)
		urequire.Equal(t, len(board.cells), Height)
		for i := 0; i < Height; i++ {
			urequire.Equal(t, len(board.cells[i]), Width)
		}
	}
	{
		var board *Board
		urequire.NotPanics(t, func() {
			board = BoardFromRepr("")
		})
		// check all squares unrevealed
		// check some mines were placed
		foundMines := false
		for i := 0; i < Width*Height; i++ {
			urequire.False(t, board.cells[i/Width][i%Width].IsRevealed)
			if board.cells[i/Width][i%Width].IsMine {
				foundMines = true
			}
		}
		urequire.True(t, foundMines)

		// check BoardFromRepr(hex).ToHexRepr() == hex
		hex := board.ToHexRepr()
		urequire.Equal(t, len(hex), 2*Width*Height, "len(hex), 2*Width*Height")
		urequire.Equal(t, hex, BoardFromRepr(hex).ToHexRepr(), "hex, BoardFromRepr(hex).ToHexRepr()")
	}
	{
		urequire.PanicsWithMessage(t, "invalid state length", func() {
			BoardFromRepr("0FaaBBccDDee")
		})
		urequire.PanicsWithMessage(t, "Error decoding hex:encoding/hex: invalid byte: 120", func() {
			BoardFromRepr("xx")
		})
	}
}

package minesweeper

import (
	"math/rand"
	"std"
	"strconv"
	"strings"

	"gno.land/p/demo/ufmt"
	"gno.land/p/grepsuzette/pr2554/v1/ternary"
)

const (
	urlImage              = "https://upload.wikimedia.org/wikipedia/commons/b/b1/497-octopus.svg"
	attributionAboveImage = "Vincent Le Moign, CC BY 4.0 <https://creativecommons.org/licenses/by/4.0>, via Wikimedia Commons"
	rRoot                 = "/r/grepsuzette/pr2554/v4"
)

var prng *rand.Rand

func Intn(n int) int {
	if prng == nil {
		// Note: our PRNG is not stateful as calling Render is not going
		// to modify this stateless realm. We initialize it here when still nil
		// this creates the randomness we need (seeded from blockchain's height)
		prng = rand.New(rand.NewPCG(uint64(std.GetHeight()), uint64(9)))
	}
	return prng.IntN(n)
}

func Render(path string) string {
	// out := "**Underwater Minesweeper**\n\n"
	out := "&nbsp;"
	out += css()
	board, x, y, _ := parsePath(path)
	lost := false
	if x != -1 && y != -1 {
		lost = !board.Reveal(x, y)
	}
	won := !lost && didWin(board)

	// if won {
	// 	out += `<center><b>You are a true underwater hero!</b><br><br>

	// <img src="` + urlImage + `" width=256 />

	// [ [New game](/r/demo/games/minesweeper) | [Back to demo/games](/r/demo/games) ]</center>
	// `
	// } else {
	// display the minefield as a matrix of buttons
	out += header(won, lost)
	repr := board.ToHexRepr()
	for j := range board.cells {
		for i := range board.cells[j] {
			out += button(i, j, board.cells[j][i], won, lost, repr, x, y)
		}
		out += "<br />"
	}
	out += footer(won, lost)
	// }

	// if debug {
	// 	out += ufmt.Sprintf(
	// 		"--- played x=%d y=%d height=%d path=%s error:%s",
	// 		x, y, std.GetHeight(), path, "",
	// 	)
	// }
	return out
}

func didWin(board *Board) bool {
	missing := 0
	for j := range board.cells {
		for i := range board.cells[j] {
			cell := &board.cells[j][i]
			if !cell.IsMine && !cell.IsRevealed {
				missing++
			}
		}
	}
	return missing == 0
}

func button(x, y int, cell Cell, won, lost bool, repr string, playedX, playedY int) string {
	var char string
	revealed := cell.IsRevealed || lost || won
	classes := []string{"btn"}
	if revealed {
		classes = append(classes, "d")
	}
	if playedX == x && playedY == y && cell.IsMine && (lost || won) {
		classes = append(classes, "exploded")
	}
	switch {
	case !revealed:
		char = "&nbsp;"
	case cell.IsMine:
		char = ternary.String(lost, "üí£", "üåà")
	case cell.AdjacentMines <= 0:
		char = "&nbsp;"
	case cell.AdjacentMines > 8:
		panic("should not happen")
	default:
		classes = append(classes, "m"+strconv.Itoa(cell.AdjacentMines))
		char = strconv.Itoa(cell.AdjacentMines)
	}
	// if ?querystring were allowed, we could then use <button> for this.
	// return "<button class='...'>" + char + "</button>"
	// instead <a> will be used for now
	link := ternary.String(lost || won, "#", ufmt.Sprintf(
		"/r/demo/games/minesweeper:state=%s&x=%d&y=%d",
		repr, x, y,
	))
	return ufmt.Sprintf(
		`<a href="%s" draggable="false" class="%s">%s</a>`,
		link, strings.Join(classes, " "), char,
	)
}

