func TestRegisterDomain(t *testing.T) {
	registry := NewDomainRegistry("GNO Name Service", "GNS")

	cases := []struct {
		name        string
		owner       std.Address
		domainName  string
		metadata    Metadata
		expectError bool
	}{
		{
			name:       "Successful Registration",
			owner:      addr1,
			domainName: "registered.gno",
			metadata: Metadata{
				Avatar:           "avatar_url",
				RegistrationTime: time.Now(),
				ExpirationTime:   time.Now().Add(365 * 24 * time.Hour),
				Description:      "A registered domain",
				ContactInfo:      "contact@registered.com",
			},
			expectError: false,
		},
		{
			name:       "Duplicate Registration",
			owner:      addr1,
			domainName: "registered.gno",
			metadata: Metadata{
				Avatar:           "avatar_url",
				RegistrationTime: time.Now(),
				ExpirationTime:   time.Now().Add(365 * 24 * time.Hour),
				Description:      "A registered domain",
				ContactInfo:      "gno_name_service@gno.land",
			},
			expectError: true,
		},
	}

	for _, c := range cases {
		t.Run(c.name, func(t *testing.T) {
			std.TestSetRealm(std.NewUserRealm(c.owner))
